// Funcion para limpiar los datos de una tabla
// nomTabla: Indica el nombre de la tabla.
// conCabecera: Indica si tiene cabecera (1 es Si, 0 es no).
// Ejemplo: Elimino datos de una tabla con cabecera  - limpiarTabla("tblResulBandeja", 1);
function limpiarTabla(nomTabla, conCabecera) {
    if (conCabecera == 1) {
        $('#' + nomTabla+' tr:not(:first)').remove();
    } else {
        $('#' + nomTabla +'').empty();
    }
}

